css:
[data-fieldname="supplier_name"] .value { font-size: 1.0em; }
[data-fieldname="note"] .value { font-size: 0.8em; }
th, td { font-size: 0.8em; }
.print-heading {padding-bottom: 0 !important;margin-bottom:0 !important;}
.table{margin:10px 0;}
.data-field{margin-top:0}

.table-bordered{border: 1px solid #000000;}
.table-bordered tbody tr th{border: 1px solid #000000; text-align: center; background-color: #bdbdbd !important;}
.table-bordered thead tr th{border: 1px solid #000000; text-align: center; background-color: #bdbdbd !important;}
.table-bordered tbody tr td{border: 1px solid #000000;}
@media print {
    body {-webkit-print-color-adjust: exact !important; color-adjust: exact !important;}
    .table-bordered tbody tr th{background-color: #bdbdbd !important; -webkit-print-color-adjust: exact !important; color-adjust: exact !important;}
    .table-bordered thead tr th{background-color: #bdbdbd !important; -webkit-print-color-adjust: exact !important; color-adjust: exact !important;}
    @page { margin : 0; }
    body { margin : 1.6cm; }
}
